<?php if (!defined('THINK_PATH')) exit();?><div class="detail-item-title level-title"><p class="title"><span>校内职务/社团活动</span></p><div class="action-box"><div class="sub-action add js_scjob_add"><i></i>添加</div></div></div><div class="show-level"><?php if(is_array($rjp["jobs"])): foreach($rjp["jobs"] as $key=>$v): ?><div class="sub-info education-info"><div class="sub-edit edit js_scjob_edit" data-id="<?php echo ($v["id"]); ?>" data-resumeid="<?php echo ($rjp["resume_id"]); ?>"><i></i>编辑</div><div class="college-info"><span><?php echo (smartRole($v["role"])); ?></span><span class="from-to"><span><?php echo (substr_replace($v["sdate"],'.',4,0)); ?></span>—<span><?php if($v["edate"] == 0): ?>至今<?php else: echo (substr_replace($v["edate"],'.',4,0)); endif; ?></span></span></div><div class="harvest"><p class="harvest-title">职务描述</p><div class="harvest-info"><div class="sub-harvest"><?php echo (smartTextarea($v["remark"])); ?></div></div></div></div><?php endforeach; endif; ?></div>