<?php if (!defined('THINK_PATH')) exit(); if(is_array($res["posts"])): foreach($res["posts"] as $k=>$v): ?><div class="list-item"><div class="post-item"><div class="item-row"><a class="row-left post-name" href="/post/<?php echo (smartBase64($v["id"])); ?>.html" target="_blank"><span class="name-box"><?php echo ($v["name"]); ?></span><?php if(!empty($v["ishot"])): ?><!--急招岗位显示img--><img class="hot-post" src="/Public/img/hot_post.png"/><?php endif; if(($v['esalary']) >= "1"): ?><span class="post-cost"><?php echo ($v['ssalary']); ?>-<?php echo ($v['esalary']); if($v['salary_unit'] == 0): ?>元/天<?php else: ?>元/月<?php endif; ?></span><?php else: ?><span class="post-cost"><?php if($v['ssalary'] <= 0): ?>面议<?php else: if($v['salary_unit'] == 0): echo ($v["ssalary"]); ?>元/天<?php else: echo ($v["ssalary"]); ?>元/月<?php endif; endif; ?></span><?php endif; ?></a><a class="row-right company-name" href="/post/company/<?php echo (smartBase64($v["org"]["id"])); ?>.html" target="_blank"><?php echo ($v["org"]["abbrname"]); ?></a></div><div class="item-row"><div class="row-left post-demand"><span class="sub-demand post-addr"><i></i><?php echo ($v["city_name"]); ?>-<?php if(!empty($v["zone_name"])): echo ($v["zone_name"]); else: ?>全市<?php endif; ?></span><span class="sub-demand degree"><i></i><?php echo (smartDegree($v["degree"])); ?></span></div><span class="row-right company-nature"><?php echo ($v["org"]["trade_name"]); ?>/<?php echo ($v["org"]["subtrade_name"]); ?></span></div><div class="item-row"><div class="row-left post-demand time"><span class="sub-demand post-time"><i></i><?php echo (smartWorkType($v["work_type"])); ?></span><?php if(!empty($v["work_days"])): ?><span class="sub-demand demand-time"><i></i><?php echo ($v["work_days"]); ?>天/周</span><?php endif; if(!empty($v["work_duration"])): ?><span class="sub-demand demand-time"><i></i>至少<?php echo ($v['work_duration']/30); ?>个月</span><?php endif; ?><!--<span class="sub-demand last-time"><i></i>距离投递结束还有<span><?php echo (smartPostStrTime($v["rev_stop_time"])); ?></span>天</span>--></div><span class="row-right publish-date"><?php echo (smartJobStrTime($v["last_time"])); ?></span></div></div><a class="company-logo" href="/post/company/<?php echo (smartBase64($v["org"]["id"])); ?>.html"><img src="<?php echo ($v["org"]["b_logo_url"]); ?>"/></a><!-- <?php echo ($v["img_url"]); ?> --></div><?php endforeach; endif; ?>