<?php if (!defined('THINK_PATH')) exit();?><div class="detail-item-title level-title"><p class="title"><span>交换生经历</span></p><div class="action-box"><div class="sub-action add js_edu_exchange_add"><i></i>添加</div></div></div><div class="show-level"><?php if(is_array($edus)): foreach($edus as $key=>$v): if(is_array($v["exchanges"])): foreach($v["exchanges"] as $key=>$vo): ?><div class="sub-info"><div class="sub-edit edit js_edu_exchange_edit" data-id="<?php echo ($vo["id"]); ?>" data-eduid="<?php echo ($v["id"]); ?>"><i></i>编辑</div><div class="college-info"><span class="name"><?php echo ($vo["school_name"]); ?></span><span class="major"><?php echo ($vo["major"]); ?></span><span class="from-to"><span><?php echo (substr_replace($vo["sdate"],'.',4,0)); ?></span>—<span><?php if($vo["edate"] == 0): ?>至今<?php else: echo (substr_replace($vo["edate"],'.',4,0)); endif; ?></span></span></div><div class="harvest"><p class="harvest-title">收获及成绩</p><div class="harvest-info"><div class="sub-harvest"><?php echo (smartTextarea($vo["remark"])); ?></div></div></div></div><?php endforeach; endif; endforeach; endif; ?></div>