<?php if (!defined('THINK_PATH')) exit();?><div class="detail-item-title"><p class="title"><span>实习经验</span></p><div class="action-box"><div class="sub-action add js_internship_add"><i></i>添加</div></div></div><?php if(is_array($internships)): foreach($internships as $key=>$v): ?><div class="sub-info education-info"><div class="sub-edit edit js_internship_edit" data-id="<?php echo ($v["id"]); ?>"><i></i>编辑</div><div class="college-info"><span class="company-tag"><?php echo ($v["company_name"]); ?></span><span class="company-tag"><?php echo ($v["nature_name"]); ?><span><?php echo (smartdictval($v["work_type"])); ?></span></span><span class="from-to"><span><?php echo (substr_replace($v["sdate"],'.',4,0)); ?></span>—<span><?php if($v["edate"] == 0): ?>至今<?php else: echo (substr_replace($v["edate"],'.',4,0)); endif; ?></span></span></div><div class="college-info"><span class="company-tag">部门：<?php echo ($v["department"]); ?></span><span class="company-tag">职能名称：<?php echo ($v["function_name"]); ?></span></div><?php if(!empty($v["work_remark"])): ?><div class="harvest"><p class="harvest-title">工作描述</p><div class="harvest-info"><div class="sub-harvest"><?php echo (smartTextarea($v["work_remark"])); ?></div></div></div><div class="harvest"><p class="harvest-title">项目经验</p><div class="harvest-info"><div class="sub-harvest"><?php echo (smartTextarea($v["work_ex"])); ?></div></div></div><?php endif; ?></div><?php endforeach; endif; ?>