<?php if (!defined('THINK_PATH')) exit();?><div class="my-avatar"><?php if(!empty($info["avatar_url"])): ?><img src="<?php echo ($info["avatar_url"]); ?>"><?php else: if($info['gender'] == 2): ?><img src="/Public/img/female.jpg"><?php else: ?><img src="/Public/img/man.jpg"><?php endif; endif; ?></div><div class="basic-info"><div class="my-name"><span class="name"><?php echo ($info["fullname"]); ?></span><span class="edit js_baseinfo_edit"><i></i>编辑</span></div><div class="my-basic"><div class="my-basic-info basic-sex"><i></i><span><?php if($res['info']['gender'] == 1): ?>男<?php else: ?>女<?php endif; ?></span></div><?php if($info['height']): ?><div class="my-basic-info basic-height"><i></i><span><?php echo ($info['height']*100); ?>CM</span></div><?php endif; ?><div class="my-basic-info basic-birth"><i></i><span><?php echo (substr($info["birthday"],0,4)); ?>年<?php echo (substr($info["birthday"],4,2)); ?>月<?php echo (substr($info["birthday"],6,2)); ?>日</span></div><div class="my-basic-info basic-org"><i></i><span><?php echo (smartResumeDict(politics,$info["politics"])); ?></span></div></div><div class="my-basic two-column my-phone"><i></i><span><?php echo ($info["phone"]); ?></span></div><div class="my-basic two-column my-mail"><i></i><span><?php echo ($info["email"]); ?></span></div><div class="my-basic addr-live"><i></i><span>实习居住地：<span><?php echo ($info["province_name"]); ?>-<?php echo ($info["city_name"]); ?>-<?php echo ($info["address"]); ?></span></span></div><div class="my-basic addr-born"><i></i><span>户口所在地：<span><?php echo ($info["hk_province_name"]); ?>-<?php echo ($info["hk_city_name"]); ?></span></span><span><?php echo (smartResumeDict(hktype,$info["hk_type"])); ?></span></div><div class="my-basic about-me"><?php if(is_array($res["info"]["tags"])): foreach($res["info"]["tags"] as $key=>$v): ?><span><?php echo ($v); ?></span><?php endforeach; endif; ?></div></div>