ovince_name"]); ?>-<?php echo ($res["zone_name"]); if(!empty($res["addr"])): ?>-<?php echo ($res["addr"]); endif; else: echo ($res["province_name"]); ?>-<?php echo ($res["city_name"]); ?>-<?php if(!empty($res["zone_name"])): echo ($res["zone_name"]); else: ?>全市<?php endif; if(!empty($res["addr"])): ?>-<?php echo ($res["addr"]); endif; endif; ?></span><!-- <span class="basic-item addr-detail"><?php echo ($res["addr"]); ?></span> --></div></div><div class="cont-item"><div class="org-img"><img src="<?php echo ($res["org"]["b_logo_url"]); ?>"/></div><div class="org-info"><p class="org-name"><?php echo ($res["org"]["fullname"]); ?></p><div class="info-box"><span class="info-item"><?php echo ($res["org"]["trade_name"]); ?></span><span class="info-item"><?php echo ($res["org"]["nature_name"]); ?></span><span class="info-item"><?php echo ($res["org"]["scale_name"]); ?></span></div></div></div><div class="cont-item"><div class="post-info"><div class="part-title"><div class="title-tag"><span></span></div><span class="title-info">岗位描述</span></div><div class="part-info"><?php echo (nl2br($res["remark"])); ?></div></div><div class="post-info"><div class="part-title"><div class="title-tag"><span></span></div><span class="title-info">岗位要求</span></div><div class="part-info"><?php echo (nl2br($res["post_remark"])); ?></div></div><?php if(!empty($res["ex2"])): ?><p class="post-sign"><i></i>该岗位由菜鸟帮帮官方合作伙伴“<?php echo ($res["ex2"]); ?>”提供</p><?php endif; ?></div><?php if(!empty($jobs["posts"]["0"]["name"])): ?><div class="cont-item"><p class="more-title">该企业更多实习岗位<span class="title-tag"></span></p><?php if(is_array($jobs["posts"])): foreach($jobs["posts"] as $key=>$v): ?><a class="more-item" href="/post/<?php echo (smartBase64($v["id"])); ?>"><div class="item-line"><span class="post-name"><?php echo ($v["name"]); ?></span><span class="post-pay"><?php if($v['ssalary'] == 0): ?>面议<?php else: if($v['salary_unit'] == 0): echo ($v["ssalary"]); ?>元/天<?php else: echo ($v["ssalary"]); ?>元/月<?php endif; endif; ?></span></div><div class="item-line"><span class="org-name"><?php echo ($v["org"]["abbrname"]); ?></span><span class="basic-item post-addr"><i></i><?php echo ($v["org"]["city_name"]); ?>-<?php if(!empty($v["org"]["zone_name"])): echo ($v["org"]["zone_name"]); else: ?>全市<?php endif; ?></span></div><div class="item-line"><div class="post-demand"><span class="sub-demand"><?php echo (smartDegree($v["degree"])); ?></span><?php if(!empty($v["work_days"])): ?><span class="sub-demand"><?php echo ($v["work_days"]); ?>天/周</span><?php endif; if(!empty($v["work_duration"])): ?><span class="sub-demand"><?php echo ($v['work_duration']/30); ?>个月</span><?php endif; ?></div><span class="post-duration"><?php echo (smartJobStrTime($v["last_time"])); ?></span></div></a><?php endforeach; endif; ?></div><?php endif; ?></div><div class="footer"><div class="logo"><img src="/Public/img/h5/logo.png"/></div><div class="slogan"><img src="/Public/img/h5/slogan.png"/></div><div class="app-download"><div class="cartoon"><img src="/Public/img/h5/cartoon.png"/></div><a class="download" href="<?php echo ($down_url); ?>"><img src="/Public/img/h5/download_btn.png"/></a></div></div></body></html>