<?php if (!defined('THINK_PATH')) exit();?><div class="detail-item-title level-title"><p class="title"><span>专业研究项目</span></p><div class="action-box"><div class="sub-action add js_edu_project_add"><i></i>添加</div></div></div><div class="show-level"><?php if(is_array($edus)): foreach($edus as $key=>$v): if(is_array($v["others"])): foreach($v["others"] as $key=>$vo): ?><div class="sub-info education-info"><div class="sub-edit edit js_edu_project_edit" data-id="<?php echo ($vo["id"]); ?>" data-eduid="<?php echo ($v["id"]); ?>"><i></i>编辑</div><div class="college-info"><span class="name">项目名称：<?php echo ($vo["name"]); ?></span><span class="major"><?php echo (smartRole($vo["role"])); ?></span><span class="from-to"><span><?php echo (substr_replace($vo["sdate"],'.',4,0)); ?></span>—<span><?php if($vo["edate"] == 0): ?>至今<?php else: echo (substr_replace($vo["edate"],'.',4,0)); endif; ?></span></span></div><div class="harvest"><p class="harvest-title">收获及成绩</p><div class="harvest-info"><div class="sub-harvest"><?php echo (smartTextarea($vo["remark"])); ?></div></div></div></div><?php endforeach; endif; endforeach; endif; ?></div>