<?php if (!defined('THINK_PATH')) exit();?><p class="others-title">相似岗位</p><?php if(is_array($simjob["posts"])): foreach($simjob["posts"] as $key=>$v): ?><div class="similar-post"><a class="similar-logo" href="/post/<?php echo (smartBase64($v["id"])); ?>.html"><img src="<?php echo ($v["org"]["b_logo_url"]); ?>"/></a><div class="similar-info"><a class="post-name" href="/post/<?php echo (smartBase64($v["id"])); ?>.html"><?php echo ($v["name"]); ?></a><!--<p class="post-pay"><?php if($v['ssalary'] == 0): ?>面议<?php else: echo ($v["ssalary"]); ?>元/天<?php endif; ?></p>--><div class="others-info"><span class="sub-demand post-addr"><i></i><?php echo ($v["city_name"]); ?></span><!--<span class="similar-company"><?php echo ($v["org"]["abbrname"]); ?></span>--></div><div class="others-info"><span class="sub-demand degree"><i></i><?php echo (smartDegree($v["degree"])); ?></span><span class="sub-demand demand-time"><i></i>3天/周</span></div></div></div><?php endforeach; endif; ?>